mapper.select:
SELECT * from ds_entity_info
where entity_name = #{entity_name}

  and id != #{entity_id}

SELECT a.id, a.entity_name, a.business, b.business_type_name AS business_name, a.datasource_id AS data_source_id, c.ds_name AS data_source_name, a.main_table AS main_table_id, d.table_english_name AS main_table_name, a.entity_field AS main_entity_field_id, e.field_english_name AS main_entity_field_name, a.non_main_table AS non_main_table_id, a.main_entity_field_name AS main_entity_field_name_id, f.field_english_name AS main_entity_field_name_name, a.non_main_table_name AS non_main_table_name from ds_entity_info a left join business_segment b on a.business = b.id left join ds_meta_info c on a.datasource_id = c.id left join ds_table_info d on a.main_table = d.id left join ds_field_info e on a.entity_field = e.id left join ds_field_info f on a.main_entity_field_name = f.id
and a.business = #{business}
and a.entity_name LIKE CONCAT(CONCAT('%',#{keywords}), '%')
