mapper.select:
select a.id AS id, IFNULL(a.field_english_name,"") AS field_english_name, IFNULL(a.field_chinese_name,"") AS field_chinese_name, IFNULL(a.table_id,"") AS table_id, IFNULL(a.datasource_id,"") AS datasource_id, IFNULL(a.field_type,"") AS field_type, IFNULL(a.field_length,"") AS field_length, IFNULL(a.isterm,"") AS isterm, IFNULL(c.table_english_name,"") AS table_name, IFNULL(b.ds_name,"") AS datasource_name from ds_field_info a LEFT JOIN ds_meta_info b ON a.datasource_id = b.id LEFT JOIN ds_table_info c ON a.table_id = c.id
and a.table_id = #{table_id}
and a.datasource_id =#{data_source_id}
and a.field_english_name LIKE CONCAT(CONCAT('%',#{keywords}), '%')
and state = 0
